    return ( 
        <>
              <Navbar></Navbar> 
              <div className='Homepage bg-middleDarkGray'>
                <div className="grid col-span-3">
                  <div className='p-2.5'>
                    <input type="text" placeholder='Seach for a friend' className='p-2.5 w-11/12 focus:outline-none w-50 border-b bg-middleDarkGray border-gray-400 text-sm'/>
                    <Conversation contacts={contacts} currentUser={currentUser}/>
                  </div>
                </div>
                <div className="grid col-span-6">
                  <div className='flex flex-col'>
                  <div className="overflow-y-scroll pr-2.5 h-3/6 scrollbar-none">
                    <div>
                      <Message/>
                      <Message own={true}/>
                      <Message/>
                      <Message/>
                    </div>
                  </div>
                  <div className="flex items-center justify-around mt-0.1">
                  <textarea
                    className="mt-0.1 p-2.5 h-11/12 w-5/6 border scrollbar-none focus:outline-none rounded-sm "
                    placeholder="write something..."
                  ></textarea>
                  <button className="h-10 w-16 border-none rounded-full cursor-pointer text-white bg-green-600" >
                    Send
                  </button>
                </div>
                  </div>
                </div>
                <div className="flex col-span-3">
                  <div className='p-2.5'>online</div>
                </div>
              </div>
        </>
     );
}
 
export default Chat;
