import React from 'react'
import { useState, useEffect} from 'react';
import {useNavigate} from 'react-router-dom';
import {toast, ToastContainer} from "react-toastify"
import "react-toastify/dist/ReactToastify.css";
import Buffer from 'Buffer'
import axios from 'axios';
import { setAvatarRoute } from '../utils/APIRoutes.js';

export default function SetAvatar () {
    const api="https://api.multiavatar.com/Binx Bond.svg";
    const navigate=useNavigate();
    const [avatars,setAvatars]=useState([]);
    const [selectedAvatar,setSelectedAvatar]=useState(undefined);
    const [isLoading,setIsLoading]=useState(true);
    const toastOptions= {
        autoClose:8000,
        pauseOnOver:true,
        draggable:true,
        theme:"light",
        
      }
    const setProfilePicture=async()=>{}
    useEffect(()=>{async function Data(){
        const data=[];
        for (let i = 0; i < 4; i++) {
            const image = await axios.get(`${api}/${Math.random()*1000}`);
            const buffer = new Buffer(image.data)
            data.push(buffer.toString("base64"))
        }
        setAvatars(data);
        setIsLoading(false);
    }
    Data(); 
    },[])
    return ( 
    <div >
        <div >
            <h1>Pick an avatar as your profile picture</h1>
        </div>
        <div className="avatars">{
            avatars.map((avatar,index)=>{
                    <div Key={index} className={`avatar ${selectedAvatar===index ? "selected":""}`}>
                        <img src={`https://api.multiavatar.com/Starcrasher.png`} alt="avatar" onClick={()=>setSelectedAvatar(index)}/>
                    </div>
                
            })
        }
        </div>
    </div> );
}
