import ChatInput from '../components/ChatInput';
import {toast, ToastContainer} from "react-toastify"
import axios from 'axios';
import {sendMessageRoute} from "../utils/APIRoutes";

export default function ChatContainer({currentChat,currentUser}) {
  const handleSendMessage=async (msg)=>{
      // alert(msg);
      await axios.post(sendMessageRoute, {
        from: data._id,
        to: currentChat._id,
        message: msg,
      });
  }
  const toastOptions= {
    autoClose:8000,
    pauseOnOver:true,
    draggable:true,
    theme:"light",
    
  }
  return (
    <>
        <div className="overflow-y-scroll pr-2.5 scrollbar-none">
                <div className='flex'>
                    <img src="assets/profil/9.jpeg" alt="Logo" className="rounded-full h-8 w-8 object-cover mr-5"/>
                    <span className="font-medium">{currentChat && currentChat.username}</span>
                </div>
                    <div>
                      <Message/>
                      <Message own={true}/>
                      <Message/>
                      <Message/>
                    </div>
        </div> 
        <ChatInput handleSendMessage={handleSendMessage}/>
    </>
  )
}
