import Message from '../components/Message';
import React,{useState,useEffect} from 'react';

import ChatInput from '../components/ChatInput';
import axios from 'axios';
import {sendMessageRoute,getAllMessagesRoute} from "../utils/APIRoutes";

export default function ChatContainer({currentChat,currentUser}) {
  const [messages, setMessages] = useState([]);
  useEffect(async()=>{
     const response=await axios.post(getAllMessagesRoute,{
      from: currentUser._id,
      to: currentChat._id,
     })
     
  },[currentChat]);
  const handleSendMsg=async (msg)=>{
      // alert(msg);
      await axios.post(sendMessageRoute, {
        from: currentUser._id,
        to: currentChat._id,
        message: msg,
      });
    // const msgs = [...messages];
    // msgs.push({ fromSelf: true, message: msg });
    // setMessages(msgs);
  }
  return (
    <>
        <div className="overflow-y-scroll pr-2.5 scrollbar-none">
                <div className='flex'>
                    <img src="assets/profil/9.jpeg" alt="Logo" className="rounded-full h-8 w-8 object-cover mr-5"/>
                    <span className="font-medium">{currentChat && currentChat.username}</span>
                </div>
                    <div>
                      {/* <Message/>
                      <Message own={true}/>
                      <Message/>
                      <Message/> */}
                    </div>
        </div> 
        <ChatInput handleSendMsg={handleSendMsg}/>
    </>
  )
}
