import Message from '../components/Message';
import React,{useState,useEffect} from 'react';
import { v4 as uuidv4 } from "uuid"
import ChatInput from '../components/ChatInput';
import axios from 'axios';
import {sendMessageRoute,getAllMessagesRoute} from "../utils/APIRoutes";

export default function ChatContainer({currentChat,currentUser}) {
  const [messages, setMessages] = useState([]);
  useEffect(()=>{async function Data() {
     const response=await axios.post(getAllMessagesRoute,{
      from: currentUser._id,
      to: currentChat._id,
     })
     setMessages(response.data);
    }
    Data();
  },[currentChat]);
  const handleSendMsg=async (msg)=>{
      // alert(msg);
      await axios.post(sendMessageRoute, {
        from: currentUser._id,
        to: currentChat._id,
        message: msg,
      });
    // const msgs = [...messages];
    // msgs.push({ fromSelf: true, message: msg });
    // setMessages(msgs);
  }
  return (
    <>
        <div className="overflow-y-scroll pr-2.5 scrollbar-none">
                <div className='flex'>
                    <img src="assets/profil/9.jpeg" alt="Logo" className="rounded-full h-8 w-8 object-cover mr-5"/>
                    <span className="font-medium">{currentChat && currentChat.username}</span>
                </div>
                <div className={own  ? "message own":"message"}>
                    <div className="flex">
               <img
                   className="rounded-full h-8 w-8 object-cover mr-5"
                   src="https://images.pexels.com/photos/3686769/pexels-photo-3686769.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
                   alt=""
            />
            <p className="messageText">Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum qui, minima suscipit saepe reiciendis beatae pariatur eos. Debitis ipsum officiis soluta minus. Animi fuga corporis repellendus eum! Voluptas, eius neque?</p>
          </div>
          <div className="text-xs mt-2.5">1 hour ago</div>
        </div>
                    <div >
                      {
                         messages.map((message)=>{
                            return (
                              <div key={uuidv4()}>
                                {message.message}
                              </div>
                            )
                        })
                      }
                      {/* <Message/>
                      <Message own={true}/>
                      <Message/>
                      <Message/> */}
                    </div>
        </div> 
        <ChatInput handleSendMsg={handleSendMsg}/>
    </>
  )
}
